package com.tns.decisionmaking;

public class Example2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4,b=6,c=12;
		if(a>b && a>c)
		{
			System.out.println(a);
		}
		else if(b>a && b>c) 
		{
			System.out.println(b);
		}
		else
		{
			System.out.println(c);
		}
	}

}
